import classes from "./../../css/Users.module.css";
import { NavLink } from 'react-router-dom';


const User = (props) => {

	let path = "/AllUsers/" + props.id;

	return (
		<NavLink to={path} className={contactA => contactA.isActive ? classes.noActive : classes.yesActive}>
			<article className={classes.item}>
				<h3 className={classes.item1}>{props.name}</h3>
				<aside className={classes.item2}>{props.age} лет</aside>
				<aside className={classes.item3}>{props.status}</aside>
				<aside className={classes.item4}>{props.country}{props.city}</aside>
			</article>
		</NavLink>
	);
}

export default User;
